
export enum ScreenView {
  MAIN_MENU,
  MUSIC,
  VIDEOS,
  PHOTOS,
  EXTRAS,
  SHUFFLE_PLAY,
  NOW_PLAYING,
  COVER_FLOW,
  VIDEO_PLAYER,
  VIDEO_LIST,
  PHOTO_VIEWER,
  ADD_YOUTUBE_VIDEO,
  ADD_IPTV_LINK,
  ADD_ONLINE_VIDEO,
  LIVE_TV,
  GAMES,
  BRICK_BREAKER,
  SNAKE,
  APPS,
  J2ME_RUNNER,
  SETTINGS,
  THEMES,
  CORS_PROXY,
  CAMERA,
  FM_RADIO,
  ADD_FM_CHANNEL,
  // Fix: Add an ACTION member to handle special menu items without navigating.
  ACTION = -1,
}

export type Theme = 'classic' | 'dark' | 'gold';

export interface MediaFile {
  id: string;
  name: string;
  url: string;
}

export interface Song extends MediaFile {
  artist: string;
  album: string;
  picture: string; // base64 encoded image
}

export interface Photo extends MediaFile {}

export interface Video extends MediaFile {
  isYoutube: boolean;
  isIPTV: boolean;
  isOnlineVideo: boolean;
}

export interface J2MEApp extends MediaFile {}

export interface FmChannel {
  id: string;
  name: string;
  url: string;
}

export interface MenuItem {
  id: ScreenView;
  name: string;
}

export interface BatteryState {
  level: number; // 0 to 1
  charging: boolean;
  supported: boolean;
}

export interface NowPlayingMedia {
  type: 'song' | 'video' | 'fm';
  index: number;
}

export interface CameraFilter {
  name: string;
  css: string;
  isVhs?: boolean;
  overlay?: string;
  vignette?: boolean;
}

